import React, { useState } from "react";
import Profile from "./Profile";
import axios from "axios";

export default function LogIn() {
  const [fullName, setFullName] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [errorMsg, setErrorMsg] = useState(false);
  const [passwordMismatchMsg, setPasswordMismatchMsg] = useState(false);
  const [successMsg, setSuccessMsg] = useState(false);
  const [showProfile, setShowProfile] = useState(false);

  //Form validation(Error/Success Handling)
  function validateForm() {
    if (!fullName) {
      setErrorMsg(true);
      setSuccessMsg(false);
      setPasswordMismatchMsg(false);
      return;
    } else if (!password) {
      setErrorMsg(true);
      setSuccessMsg(false);
      setPasswordMismatchMsg(false);
      return;
    } else if (!confirmPassword) {
      setErrorMsg(true);
      setSuccessMsg(false);
      setPasswordMismatchMsg(false);
      return;
    } else if (password !== confirmPassword) {
      setErrorMsg(false);
      setPasswordMismatchMsg(true);
      setSuccessMsg(false);
      return;
    } else {
      setErrorMsg(false);
      setPasswordMismatchMsg(false);
      setSuccessMsg(true);
      (async function () {
        // const res = await fetch("https://dummyjson.com/auth/login", {
        //   method: "POST",
        //   headers: { "Content-Type": "application/json" },
        //   body: JSON.stringify({
        //     username: 'hbingley1', //hbingley1
        //     password: 'CQutx25i8r', //CQutx25i8r
        //     // expiresInMins: 60, // optional
        //   }),
        // });
        // const data = await res.json()
        // console.log(data);
        const res = await axios.post("https://dummyjson.com/auth/login", {
          username: "hbingley1", //hbingley1
          password: "CQutx25i8r", //CQutx25i8r
        });
      })();
      setTimeout(() => {
        setShowProfile(true);
      }, 1000);
    }
  }
  return (
    <>
      {!showProfile && (
        <form
          onSubmit={(e) => {
            e.preventDefault();
            validateForm();
          }}
        >
          <h2>LogIn</h2>
          <div>
            <label for="full-name">Full Name</label>
            <input
              id="full-name"
              placeholder="Enter your fullname"
              type="text"
              onChange={(e) => setFullName(e.target.value)}
            />
          </div>
          <div>
            <label for="password">Password</label>
            <input
              id="password"
              placeholder="Enter password"
              type="password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div>
            <label for="confirm-password">Confirm Password</label>
            <input
              id="confirm-password"
              placeholder="Enter confirm password"
              type="password"
              onChange={(e) => setConfirmPassword(e.target.value)}
            />
          </div>
          <div className="error-success-msg">
            {errorMsg && <p id="error-msg">All the fields are mandatory</p>}
            {passwordMismatchMsg && (
              <p id="error-msg">Password and Confirm Password not matching</p>
            )}
            {successMsg && <p id="success-msg">Successfully LogedIn!!</p>}
          </div>
          <input type="submit" value="LogIn" id="signup-btn" />
        </form>
      )}
      {showProfile && <Profile fullName={fullName} password={password} />}
    </>
  );
}
